function UtilityKaraoke (selectorText) {
  var letterId = 0;
  var currentLetterId = 0;
  var listWord = [];
  var that = this;
  this.init = function() {
    $(selectorText).each(function() {
      var text = $(this).text();
      var timeEnd = $(this).attr('data-end');
      var timeBegin = $(this).attr('data-begin');
      var html = '';
      var partsText = getString(text);
      partsText[0][0] = timeBegin;
      partsText[partsText.length - 1][2] = timeEnd;
      for (var i in partsText) {
        var timeDur= partsText[i][2] - partsText[i][0];
        var timeDurLetter = timeDur/partsText[i][1].length;

        for (var j in partsText[i][1]) {
          html += '<span id="l'
            + letterId
            + '" class="h-karaoke">'
            + partsText[i][1][j]
            + '</span>';
          if (j == 0) {
            listWord[letterId] = [parseFloat(partsText[i][0]), timeDurLetter];
          } else {
            listWord[letterId] = [parseFloat(listWord[letterId - 1][0]) + parseFloat(listWord[letterId - 1][1]),
              timeDurLetter];
          }
          letterId ++;
        }
      }
      $(this).html(html);
    });
  };

  this.rend = function() {
    if (audio.paused) {
      return 0;
    }

    if (audio.currentTime <= listWord[0][0]) {
      currentLetterId = 0;
    }

    if (currentLetterId >= listWord.length) {
      currentLetterId = listWord.length-1;
    }

    var ftemp = setTimeout(function() {
      if (listWord[currentLetterId][0] < audio.currentTime) {
        $('#l' + currentLetterId).addClass('speaking');
        currentLetterId++;
      }
      that.rend();
    }, currentLetterId < listWord.length ? 1 * listWord[currentLetterId][1] : 500);
  };

  this.refresh = function() {
    $('.h-karaoke').removeClass('speaking');
    currentLetterId = 0;
  }
  this.setCurrentLetterId = function(number) {
    currentLetterId = number;
  }
}

function getString(str) {
  var alphabets = str.split(/\[[0-9]*[.][0-9]+\]/);
  var numbers = str.match(/\[[0-9]*[.][0-9]+\]/g);
  var result = [];

  if (numbers == null) {
    result.push([0, alphabets[0], 0]);
  } else {
    for(var i = 0; i < numbers.length; i++) {
      numbers[i] = parseFloat(numbers[i].substring(1, numbers[i].length - 1));
    }
    for (i = 0; i < alphabets.length; i++) {
      result.push([numbers[i-1] == null ? 0 : numbers[i-1], alphabets[i], numbers[i] == null ? 0 : numbers[i]]);
    }
  }
  return result;
}

function UtilityTextAutoFit(selector) {
  if ($('#text_autofit').length == 0) {
    $("body").append(
      "<div id='text_autofit' style='position:absolute; visibility:hidden;\
            height:auto; width:auto; white-space:nowrap; top:0; left:0;'>\
      </div>"
    );
  }

  var measure = $('#text_autofit');
  $(selector).each(function() {
    var dom = $(this);
    var font_size = parseFloat(dom.css("font-size"));
    measure.html(dom.text());
    measure.css("font-size", dom.css("font-size"));
    measure.css("font-family", dom.css("font-family"));
    measure.css("font-weight", dom.css("font-weight"));
    while (measure.width() >= dom.width()) {
      font_size -= 0.1;
      dom.css("font-size", font_size + "px");
      measure.css("font-size", font_size + "px");
    }
  })
}

