function UtilityPlayAudio(selector) {
  var audio_id = $(selector).data('audio-src').replace(/[^\w]/g, '');
  if ($('#' + audio_id).length == 0) {
    $("body").append(
      "<audio id='" + audio_id + "'>\
        <source src='" + $(selector).data('audio-src') + "' type='audio/mpeg'>\
      </audio>"
    );
  }

  var begin = $(selector).data('audio-begin');
  var end = $(selector).data('audio-end');
  if (begin === undefined) begin = 0;
  $(selector).click(function() {
    audio = $('#' + audio_id);
    if (!audio.prop("paused")) return;

    audio.prop("currentTime", begin);
    audio.trigger('play');
    if (end !== undefined) {
      setTimeout(function() {
        audio.trigger('pause');
      }, (end - begin) * 1000);
    }
  });
}

function ZoomAll () {
  this.keyPressing = [];
  this.scale = 1;
  this.scale_text = 1;

  this.max_scale = 1.5;
  this.min_scale = 0.5;
  this.detal_scale = 0.05;
  this.min_scale_text = 0.5;
  this.max_scale_text = 2;

  this.clicked = false;
  this.clickX;
  this.clickY;

  this.content_bound = $("#content-bound");
  this.layout_module = $("#layout-module");
  this.exercise_name = $("#exercise-name");

  this.init = function() {
    // var scaleTextCookie = parseFloat($.cookie("scaleTextCookie"));
    // if(!isNaN(scaleTextCookie) && scaleTextCookie != 1) {
    //   this.scale_text = scaleTextCookie;
    //   this.executeZoomText(scaleTextCookie);
    // }

    var default_text_size = parseFloat(EXERCISE.default_text_size) || 1;
    this.executeZoomText(default_text_size);

    $this = this;
    $(document).on('click', '#zoom_fit', function() {
      $this.zoomFit();
    });
    $(document).on('click', '#zoom_in_text', function() {
      $this.zoomInText();
    });
    $(document).on('click', '#zoom_out_text', function() {
      $this.zoomOutText();
    });
    $(document).on('click', '#zoom_in', function() {
      $this.zoomIn();
    });
    $(document).on('click', '#zoom_out', function() {
      $this.zoomOut();
    });
    $(document).on('click', '#disable_zoom', function() {
      $this.disable();
    });

    var image = $("#layout-module img:not(.no-zoom)");
    image.each(function() {
      $(this).data("original-height", $(this).height());
      $(this).data("original-width", $(this).width());
    });

    $(document).keydown(function(event) {
      $this.keyPressing[event.keyCode] = true;
      if($this.keyPressing[65] && $this.keyPressing[187])
        $this.zoomInText();
      if($this.keyPressing[65] && $this.keyPressing[189])
        $this.zoomOutText();
    });

    $(document).keyup(function(event) {
      delete $this.keyPressing[event.keyCode];
    })
  };

  this.handScroll = function() {
    $('img').on('dragstart', function(event) {
      event.preventDefault();
    });

    this.content_bound.on({
      'mousemove': function(e) {
        if (this.clicked) {
          $('html').css('cursor', 'all-scroll');
          $("#content-bound").scrollTop( $("#content-bound").scrollTop() + (this.clickY - e.pageY) );
          $("#content-bound").scrollLeft( $("#content-bound").scrollLeft() + (this.clickX - e.pageX) );
          this.clickY = e.pageY;
          this.clickX = e.pageX;
        }
      },
      'mousedown': function(e) {
        this.clicked = true;
        this.clickY = e.pageY;
        this.clickX = e.pageX;
      },
      'mouseup': function() {
        this.clicked = false;
        $('html').css('cursor', 'auto');
      }
    });
  }

  this.disableHandScroll = function() {
    this.content_bound.off('mousemove');
    this.content_bound.off('mousedown');
    this.content_bound.off('mouseup');
    $('img').off('dragstart');
  }

  this.execute = function(z) {
    this.layout_module.css("transform", "scale(" + z + ")");

    if (this.scale > 1) {
      var w = Math.abs(this.layout_module.width() - this.content_bound.width())/z;
      var h = Math.abs(this.layout_module.height() - this.content_bound.height())/z;

      var transform_origin = "" + (- Number(w) - Number(z * this.exercise_name.width())) + "px "
                                + (- Number(h) - Number(z * this.exercise_name.height())) + "px";

      var exercise_name_transform_origin = "" + (- Number(w)) + "px "
                                              + (- Number(h)) + "px";

      this.layout_module.css("transform-origin", transform_origin);
      this.layout_module.css("-ms-transform-origin", transform_origin);
      this.layout_module.css("-webkit-transform-origin", transform_origin);
    } else {
      this.layout_module.css("transform-origin", "top");
      this.layout_module.css("-ms-transform-origin", "top");
      this.layout_module.css("-webkit-transform-origin", "top");
    }
  };

  this.executeZoomText = function(z) {

    var notZoomTag = $(".picture-index,.no-zoom,.no-zoom *,.utility-record-controller,.utility-record-controller *");
    var notZoomTagSize = notZoomTag.map(function() {
      return {
        fontSize: parseFloat($(this).css("font-size")),
        marginTop: parseFloat($(this).css("margin-top")),
        marginBottom: parseFloat($(this).css("margin-bottom")),
        paddingTop: parseFloat($(this).css("padding-top")),
        paddingBottom: parseFloat($(this).css("padding-bottom")),
        lineHeight: parseFloat($(this).css("line-height"))
      };
    });

    var allTags = $("#exercise-content *:not(style,script)");
    var newSizes = allTags.map(function() {
      return {
        fontSize: parseFloat($(this).css("font-size")) * z,
        marginTop: parseFloat($(this).css("margin-top")) * z,
        marginBottom: parseFloat($(this).css("margin-bottom")) * z,
        paddingTop: parseFloat($(this).css("padding-top")) * z,
        paddingBottom: parseFloat($(this).css("padding-bottom")) * z,
        lineHeight: parseFloat($(this).css("line-height")) * z
      };
    });

    for (var i = 0; i < allTags.length; i++) {
      $(allTags[i]).css({
        "font-size": newSizes[i].fontSize + 'px',
        "margin-top": newSizes[i].marginTop + 'px',
        "margin-bottom": newSizes[i].marginBottom + 'px',
        "padding-top": newSizes[i].paddingTop + 'px',
        "padding-bottom": newSizes[i].paddingBottom + 'px',
        "line-height": newSizes[i].lineHeight + 'px'
      });
    }

    for (var i = 0; i < notZoomTag.length; i++) {
      $(notZoomTag[i]).css({
        "font-size": notZoomTagSize[i].fontSize + 'px',
        "margin-top": notZoomTagSize[i].marginTop + 'px',
        "margin-bottom": notZoomTagSize[i].marginBottom + 'px',
        "padding-top": notZoomTagSize[i].paddingTop + 'px',
        "padding-bottom": notZoomTagSize[i].paddingBottom + 'px',
        "line-height": notZoomTagSize[i].lineHeight + 'px'
      });
    }
    $(window).trigger("zoomtextevent");
  }

  this.enable = function() {
    $("body").addClass("block-select");
    this.handScroll();
  };

  this.disable = function() {
    if ($("body").hasClass("block-select")) {
      $("body").removeClass("block-select");
    }
    this.disableHandScroll();
    this.reset();
  };

  this.reset = function() {
    this.scale = 1;

    this.execute(this.scale);
    this.executeZoomText(1 / this.scale_text);

    this.scale_text = 1;
    $.cookie("scaleTextCookie", "NaN");

    var image = $("#layout-module img:not(.no-zoom)");
    image.each(function() {
      $(this).height($(this).data("original-height"));
      $(this).width($(this).data("original-width"));
    })
  };

  this.zoomInText = function() {
    if(this.scale_text > this.max_scale_text)
      return;
    this.scale_text *= 1.1;
    this.executeZoomText(1.1);
    $.cookie("scaleTextCookie", this.scale_text);
  }

  this.zoomOutText = function() {
    if(this.scale_text < this.min_scale_text)
      return;
    this.scale_text /= 1.1;
    this.executeZoomText(1 / 1.1);
    $.cookie("scaleTextCookie", this.scale_text);
  }
  this.zoomFitImage = function() {
    var isBreak = false;
    var contentBound = $("#content-bound")[0];

    while(contentBound.scrollHeight > contentBound.clientHeight) {
      var oldScrollHeigth = contentBound.scrollHeight;
      var image = $("#layout-module img:not(.no-zoom)");
      image.each(function() {
        if($(this).height() < 100) {
          isBreak = true;
          return false;
        }
      })
      if(isBreak)
        break;

      image.each(function() {
        var height = $(this).height()
        $(this).height(height * 95 / 100);
        var width = $(this).width()
        $(this).width(width * 95 / 100);
      })

      var newScrollHeight = contentBound.scrollHeight;
      if(newScrollHeight == oldScrollHeigth)
        break;
    }
  }
  this.zoomFit = function() {
    if(this.scale_text <= this.min_scale_text)
      return;
    var contentBound = $("#content-bound")[0];
    var isBreak = false;
    while(contentBound.scrollHeight > contentBound.clientHeight) {
      var oldScrollHeigth = contentBound.scrollHeight;
      this.zoomFitImage();
      var newScrollHeight = contentBound.scrollHeight;
      if(contentBound.scrollHeight == contentBound.clientHeight)
        break;

      var oldScrollHeigth = contentBound.scrollHeight;
      this.scale_text *= 0.95;
      this.executeZoomText(0.95);
      var newScrollHeight = contentBound.scrollHeight;
      if(contentBound.scrollHeight == contentBound.clientHeight)
        break;
      if(this.scale_text <= this.min_scale_text)
        break;
    }
  }

  this.zoomIn = function() {
    if (!($("body").hasClass("block-select"))) {
      this.enable();
    }

    if (this.scale < this.max_scale) {
      this.scale += this.detal_scale;
      this.execute(this.scale);
    }
  };

  this.zoomOut = function() {
    if (!($("body").hasClass("block-select"))) {
      this.enable();
    }

    if (this.scale > this.min_scale) {
      this.scale -= this.detal_scale;
      this.execute(this.scale);
    }
  };

  this.autoTextSize = function() {
    var contentBound = $("#content-bound")[0];
    var oldScrollHeigth = contentBound.scrollHeight;
    var newScrollHeight = 0;
    do {
      if(this.scale_text > 1.5)
        break;
      this.scale_text *= 1.1;
      this.executeZoomText(1.1);

      newScrollHeight = contentBound.scrollHeight;
    } while (oldScrollHeigth >= newScrollHeight)

    if(newScrollHeight > oldScrollHeigth) {
      this.scale_text /= 1.1;
      this.executeZoomText(1/1.1);
    }
  }
}

