function UtilityTyping(selector) {
  var baseUrl = "https://nlp.sachmem.vn/grader_api/v3";
  var $this = this;

  this.enableEffect = function() {
    $(selector).each(function() {
      if (!$(this).hasClass('correct')) {
        $(this).prop('contenteditable', true);
        $(this).removeClass("correct");
        $(this).removeClass("wrong");
        $(this).text('');
        $(this).val('');
        $(this).data('result', null);
        $(this).data('percentage', null);
        $(this).removeAttr('title').tooltip('destroy');
        $(this).next('span.percentage').remove();
        $(this).next('.typing-question-icon').remove();
      }
    });
  };

  this.disableEffect = function() {
    $(selector).prop('contenteditable', false);
  };

  this.letCheck = function(callback, disableShowPercentage) {
    this.disableEffect();

    //not check, show answers as suggestion
    if(input.skip_checking) {
      if($(selector + " .correct").length) return;

      $(selector).each(function() {
        var key = $(this).data("key").toString().trim()
                         .replace(/\|/g, "<span style='color:red!important'> / </span>");
        key = "<span class='correct'>" + key + "</span>";
        var userkey = $(this).text().trim();
        userkey = userkey ? (userkey + '<span style="color:#333!important"> | </span>') : userkey;
        $(this).html([userkey, key]);
      });
      return;
    }
    //

    if(!$this.finishChecking(callback, disableShowPercentage)) {
      $(selector).each(function(index) {
        if($(this).data('result') === null) {
          var input = this;
          $(input).addClass('loading');
          $this.singleInputCheck(input, function() {
            $(input).removeClass('loading');
            $this.finishChecking(callback, disableShowPercentage);
          });
        }
      });
    }
  };

  this.singleInputCheck = function(input, singleCheckFinishCallBack) {
    input = $(input);
    var key = removeContractions(input.data('key')).split("|");
    for(var i = 0; i < key.length; i++) {
      key[i] = key[i].replace(/\s+/g, ' ').trim();
    }
    var user_answer = removeContractions(input.text()).replace(/\s+/g, ' ').trim()

    if (key.indexOf(user_answer) >= 0) {
      setInputData(input, true, 1, null, singleCheckFinishCallBack);
    } else if(user_answer == "") {
      setInputData(input, false, 0, null, singleCheckFinishCallBack);
    } else {
      var context = input.data('context') || '';
      context = getSentenceContext(context.replace(/\(.*?\)/g, ' ').replace(/\s+/g,' ').trim());
      $.ajax({
        url: baseUrl + "/grader/get_similarity",
        data: JSON.stringify({
          option: context ? 'IN_QUESTION' : 'ONLY_ANSWER',
          question: context ? context : '',
          answers:  key,
          user_answer: user_answer,
          scope: "sim grammar score"
        }),
        dataType: 'json',
        contentType: "application/json; charset=utf-8",
        method: 'POST',
        success: function(data, status){
          if(data.error) {
            setInputData(input, false, 0, null, singleCheckFinishCallBack);
            return;
          }
          result = context ? data.answers_in_question : data.only_answers;
          var score = data.score;
          score = Math.round(score*100)/100;
          var errors = result.grammar_checking.errors;
          setInputData(input, score >= 0.9, score, errors, singleCheckFinishCallBack);
        },
        error: function(data, status) {
          setInputData(input, false, 0, null, singleCheckFinishCallBack);
        }
      });
    }
    function setInputData(input, result, percentage, errors, singleCheckFinishCallBack) {
      input.data("result", result);
      input.data("percentage", percentage);
      input.data("errors", errors);
      singleCheckFinishCallBack && singleCheckFinishCallBack();
    }
    // Lay ra cau chua input
    function getSentenceContext(context) {
      if(!context) return '';
      var sentences = context.match( /[^\.!\?]+([\.!\?]+|$)/g );
      for(var i=0; i<sentences.length; i++) {
        if(sentences[i].indexOf('{}') >= 0) {
          return sentences[i].trim();
        }
      }
      return '';
    }
  }


  this.finishChecking = function(callback, disableShowPercentage) {
    var allChecked = true;
    var result = [];
    var typing = $(selector)
    for(var i = 0; i < typing.length; i++) {
      result.push($(typing[i]).data("result"));
      if($(typing[i]).data("result") === null) {
        allChecked = false;
        break;
      }
    }
    if(allChecked) {
      $(selector).each(function() {
        if($(this).data('result'))
          $(this).addClass("correct");
        else
          $(this).addClass("wrong");
      });
      UtilityTextNormalize($(selector).filter('.correct'));
      !disableShowPercentage && $(selector).each(function() {
        // $(this).append("<span class='percentage'>" + $(this).data("percentage")*100 + "%</span>");
        $(this).attr({'title': "Độ chính xác: " + parseInt($(this).data("percentage")*100) + "%. Chấm điểm tự động bởi Sách Mềm", "data-container": "body"}).tooltip('fixTitle');

        // if($(this).next().hasClass('percentage')) return true;
        // var percentage = Math.round($(this).data("percentage")*100);
        // var span = $("<span class='percentage'>" + percentage + "%</span>");
        // span.insertAfter(this);
        // span.attr({'title': "Độ chính xác: " + percentage + "%. Chấm điểm tự động bởi Sách Mềm", "data-container": "body"}).tooltip('fixTitle');

        if($(this).next().hasClass('typing-question-icon')) return true;
        var errors = $(this).data("errors");
        if(errors && errors.length) {
          var questionIcon = $("<i class='fa fa-question-circle typing-question-icon'></i>");
          questionIcon.insertAfter(this);
          $(questionIcon).on("mouseenter", function() {
            var tooltip = $("<ul class='typing-message-tooltip'></ul>");
            for(var i=0; i<errors.length; i++) {
              tooltip.append("<li>" + errors[i].message + "</li>")
            }
            var questionIconBounding = this.getBoundingClientRect();
            tooltip.css({
              width: 450,
              top: questionIconBounding.top,
              left: questionIconBounding.left,
              transform: 'translateX(-50%) translateY(calc(-100% - 5px))',
              maxWidth: 'calc(100vw - 30px)'
            });
            $("body").append(tooltip);
            console.log(tooltip[0].getBoundingClientRect())

            if(tooltip[0].getBoundingClientRect().left < 0) {
              tooltip.css({
                left: '5px',
                transform: 'translateY(calc(-100% - 10px))'
              });
            } else if(tooltip[0].getBoundingClientRect().right > window.innerWidth) {
              tooltip.css({
                left: 'initial',
                right: '5px',
                transform: 'translateY(calc(-100% - 5px))'
              });
            }
          }).on("mouseleave", function() {
            $(".typing-message-tooltip").remove();
          })
        }
      })
      if(callback) {
        callback(result);
      }
    }
    return allChecked;
  }

  $(selector).on("blur", function() {
    $this.singleInputCheck(this);
  });
}

