function UtilityShuffleArray() {
  var key;
  this.encode = function(origin_array) {
    key = [];
    var array = origin_array.slice(), temp, j;
    for (var i = array.length - 1; i >= 0; i--) {
      j = Math.floor(Math.random() * i);
      key.push(j);
      temp = array[i];
      array[i] = array[j];
      array[j] = temp;
    }
    return array;
  };
  this.decode = function(shuffled_array) {
    var array = shuffled_array.slice(), temp, j;
    for (var i = 0; i < array.length; i++) {
      j = key[array.length - i - 1];
      temp = array[i];
      array[i] = array[j];
      array[j] = temp;
    }
    return array;
  }
}

function UtilityDragDrop(drag, drop, container) {
  var installed = false;

  this.startThis = function(event, ui) {
    $(this).draggable('option', 'revert', true);
    $("#sound_effect_drag").trigger('play');
  };

  this.stopDrag = function(event, ui) {
    $("#sound_effect_drop").trigger('play');
  };

  this.dropThis = function(event, ui) {
    $(this).draggable('option', 'revert', true);
    $(this).animate({top: 0, left: 0}, 500);
    $(this).css("text-align","center");
    ui.draggable.draggable('option', 'revert', true)
    if($(this).data("origin-text") != null && $(this).data("origin-text") != "")
      $(this).text($(this).data("origin-text"));
  };

  this.handleDrop = function(event, ui) {
    var slotKey = $(this).attr("data-key").trim();
    var cardKey = $(ui.draggable.context).attr("data-answer").trim();
    if($(this).data("first-word") == true) {
      var text = $(ui.draggable.context).text().trim();
      $(ui.draggable.context).data("origin-text", text);
      $(ui.draggable.context).text(text[0].toUpperCase() + text.slice(1));
    }

    ui.draggable.position({of: $(this), my: 'left top', at: 'left top'});
    ui.draggable.draggable('option', 'revert', false);
    $(ui.draggable.context).css("text-align","left");
    if (slotKey == cardKey) {
      $(this).data('result', 'correct');
    } else {
      $(this).data('result', 'wrong');
    }
    $(this).data("user-answer", $(ui.draggable.context).data("answer"));
  };

  this.enableEffect = function() {
    $(drop).removeClass('ui-droppable');
    $(drop).removeClass('correct');
    $(drop).removeClass('wrong');
    $(drop).data('result', '');
    $(drop).data('user-answer', '');
    $(drag).removeClass('ui-draggable');
    $(drag).removeClass('ui-droppable');
    $(drag).css({"top": 0, "left": 0, "text-align": "center", "cursor": "pointer"});

    if (!installed) {
      $(drag).draggable({
        containment: container,
        stack: drag,
        revert: true,
        start: this.startThis,
        stop: this.stopDrag
      }).droppable({
        accept: drag,
        hoverClass: 'hovered',
        drop: this.dropThis
      });
      $(drop).droppable({
        accept: drag,
        hoverClass: 'hovered',
        drop: this.handleDrop
      });
      installed = true;
    } else {
      $(drag).draggable('enable');
    }
    $(drag).on("click",function() {
      if(($(this).css("top") != "0px" && $(this).css("top") != "auto")
        || ($(this).css("left") != "0px" && $(this).css("left") != "auto"))
        return;
      var dropElement;
      for(var i = 0; i < $(drop).length; i++) {
        if($(drop).eq(i).data("result") == "") {
          dropElement = $(drop).eq(i);
          break;
        }
      }
      $(this).matchToDrop(dropElement);
    }).each(function() {
        if($(this).data("origin-text") != null && $(this).data("origin-text") != "")
          $(this).text($(this).data("origin-text"));
    })
  };

  this.disableEffect = function() {
    $(drag).draggable({disabled: true}).unbind("click");
    $(drag).css("cursor", "auto");
  };

  this.letCheck = function() {
    var result = [];
    $(drop).each(function() {
      if ($(this).data('result') == 'correct') {
        $(this).addClass('correct');
        result.push(true);
      } else {
        $(this).addClass('wrong');
        result.push(false);
      }
    });
    return result;
  }
}

function UtilityDragDropMultiple(drag, drop, container, isMultipleDrag) {
  var installed = false;
  drag += ":not(:empty)";

  this.startThis = function(event, ui) {
    $("#sound_effect_drag").trigger('play');
  };

  this.handleDrop = function(event, ui) {
    var drop = this
    setTimeout(function() {
      if($(drop).is(':hover') || isMobile()) { // sua loi span nhieu dong se drop nhieu lan
        var user_answer = $(ui.draggable.context).text();
        var key = $(drop).data('key');
        // Tu dong viet hoa, thuong khi tha
        if(/^[A-Z]/.test(key)) {
          user_answer = user_answer.replace(/(^|\/)\s*[A-z]/g, function(match) {
            return match.toUpperCase();
          });
        } else if(/^[a-z]/.test(key)) {
          user_answer = user_answer.replace(/(^|\/)\s*[A-z]/g, function(match) {
            return match.toLowerCase();
          });
        }
        $(drop).text(user_answer);
        !isMultipleDrag && $(ui.draggable.context).draggable('disable').addClass('disabled');
        !isMultipleDrag && $($(drop).data('dragElement')).draggable('enable').removeClass('disabled');
        $(drop).data('dragElement', ui.draggable.context);
        $("#sound_effect_drop").trigger('play');
      }
    }, 100)
  };

  this.enableEffect = function() {
    $(drag).removeClass('ui-draggable disabled');
    $(drop).removeClass('ui-droppable');
    $(drop).removeClass('correct');
    $(drop).removeClass('wrong');
    $(drop).data('dragElement', null);
    $(drop).text('');

    if (!installed) {
      $(drag).draggable({
        containment: container,
        helper: function(e) {
          var clone = $(e.target).clone();
          if(e.target.offsetWidth > 150) {
            clone.css({"width": 150, 
              "overflow": "hidden", 
              "text-overflow": "ellipsis", 
              "white-space": "nowrap", 
              "height": "auto",
            });
          } else {
            clone.css("width", e.target.offsetWidth + 1);
          }
          return clone;
        },
        stack: drag,
        start: this.startThis,
        cursorAt: {top: 15, left: 40 }
      });
      $(drop).droppable({
        accept: drag,
        hoverClass: 'hovered',
        drop: this.handleDrop,
        tolerance: 'pointer'
      });
      installed = true;
    } else {
      $(drag).draggable('enable');
    }
  };

  this.disableEffect = function() {
    $(drag).draggable({disabled: true});
  };

  this.letCheck = function() {
    var result = [];
    $(drop).each(function() {
      var keys = $(this).data('key').toString().trim().toLowerCase().split('|');
      var user_answer = $(this).text().toString().toLowerCase();
      if (keys.indexOf(user_answer) >= 0 ) {
        $(this).addClass('correct');
        result.push(true);
      } else {
        $(this).addClass('wrong');
        result.push(false);
      }
    });
    return result;
  }
}

function UtilityActiveRecorder() {
  var audioContext;
  var audioRecorder;
  var _realAudioInput;

  var recognition;
  if ('webkitSpeechRecognition' in window) {
    recognition = new webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-GB';
  }

  function handlerStartUserMedia(stream) {
    _realAudioInput = audioContext.createMediaStreamSource(stream);
    audioRecorder = new Recorder(_realAudioInput);
  }

  function handlerErrorUserMedia(e) {
    console.log("Error no user media!");
  }

  window.onload = function init() {
    window.AudioContext = window.AudioContext || window.webkitAudioContext || window.mozAudioContext;
    navigator.getUserMedia = (navigator.getUserMedia || navigator.webkitGetUserMedia ||
                              navigator.mozGetUserMedia || navigator.msGetUserMedia);
    window.URL = window.URL || window.webkitURL;
    audioContext = new AudioContext;
    navigator.getUserMedia({vide:false, audio: true}, handlerStartUserMedia, handlerErrorUserMedia);
  };

  $(".utility-record-controller").append("\
    <button class='utility-recorder'\
               data-toggle='tooltip'\
               data-placement='top'\
               data-original-title='nhấn và giữ để ghi âm'\
               data-trigger='hover'></button>\
    <span class='utility-recording-icon'></span>\
    <span class='utility-recording-time'></span>\
    <button class='utility-player'></button>\
  ");
  $(".utility-recording-icon").hide();
  $(".utility-recording-time").hide();
  $(".utility-player").css("visibility", "hidden");

  $(window).load(function() {
    $(".utility-record-controller").each(function() {
      var recordController = this;
      var recorderHtml = $(this).children(".utility-recorder");
      var effect;
      var effectLoop;
      var effectHtml = $(this).children(".utility-recording-icon");
      var recordTime;
      var recordTimeLoop;
      var recordTimeHtml = $(this).children(".utility-recording-time");
      var playerHtml = $(this).children(".utility-player");

      recorderHtml.mousedown(function() {
        if (!audioRecorder) return;
        audioRecorder && audioRecorder.record(recorderHtml.parent());
        recorderHtml.siblings("audio").remove();
        recorderHtml.addClass("recording");
        playerHtml.removeClass("playing");
        effectHtml.show();
        recordTimeHtml.show();
        playerHtml.css("visibility", "hidden");

        effect = 0;
        effectHtml.fadeOut(500);
        effectLoop = setInterval(function() {
          if (effect == 0) {
            effect = 1;
            effectHtml.fadeIn(500);
          } else {
            effect = 0;
            effectHtml.fadeOut(500);
          }
          if (!recorderHtml.hasClass("recording")) effectHtml.hide();
        }, 500);

        recordTime = 0.0;
        recordTimeLoop = setInterval(function() {
          recordTime += 0.1;
          recordTimeHtml.html(recordTime.toFixed(1) + 's');
          if (!recorderHtml.hasClass("recording")) recordTimeHtml.hide();
        }, 100);

        if(recognition) {
          recognition.start();
          playerHtml.attr({'title': "...", "data-container": "body"}).tooltip('fixTitle');
          recognition.onresult = function(event) {
            var transcript = "";
            for (var i = 0; i < event.results.length; ++i) {
              if (event.results[i].isFinal) {
                transcript += event.results[i][0].transcript;
              }
            }
            playerHtml.attr({'title': transcript}).tooltip('fixTitle');
          };
        }

        function mouseUpCallBack() {
          setTimeout(function() {
            if (!audioRecorder) return;
            audioRecorder && audioRecorder.stop();

            if (recorderHtml.hasClass("recording")) {
              recorderHtml.removeClass("recording");
              effectHtml.hide();
              recordTimeHtml.hide();
              playerHtml.css("visibility", "visible");
              playerHtml.css("display", "inline-block");
            }
            window.clearInterval(recordTimeLoop);
            window.clearInterval(effectLoop);
          }, 200);

          if(recognition) {
            recognition.stop();
          }

          setTimeout(function() {
            if($(recordController).data("origin-text")) {
              var origin_word = $(recordController).data("origin-text");
              var speaker_word = playerHtml.attr("data-original-title");

              $.ajax({
                method: "GET",
                data: {
                  origin_word: origin_word,
                  speaker_word: speaker_word
                },
                dataType: "json",
                url: "https://speakchecker.herokuapp.com/words",
                success: function (response) {
                  playerHtml.attr({'title': speaker_word + " (" + Math.round(response.rate * 100) + "%)"})
                            .tooltip('fixTitle').tooltip("show");
                }
              });
            }
          }, 500)
          window.removeEventListener("mouseup", mouseUpCallBack);
        }
        window.addEventListener("mouseup", mouseUpCallBack);
      });

      playerHtml.click(function() {
        if (!playerHtml.hasClass("playing")) {
          playerHtml.addClass("playing");
          playerHtml.siblings("audio").first().trigger("play");
        } else {
          playerHtml.removeClass("playing");
          playerHtml.siblings("audio").first().trigger("pause");
        }
      });
    });
  });
}

function UtilityHover(container, hoverCallBack, clickCallBack) {
  this.container = $(container);
  this.objects = this.container.find(".object").sort(function(a, b){
    return parseInt($(a).data("z-index")) < parseInt($(b).data("z-index")) ? 1 : -1;
  });
  this.recorders = this.container.find(".utility-record-controller");
  this.editingObject = null;
  this.canvas = document.createElement("canvas");
  this.canvas.width = 1000;
  this.canvas.height = 1000;
  this.context = this.canvas.getContext("2d");

  this.container.css({position: "relative", "max-width": "100%"});
  this.container.find("img").attr({draggable: false}).addClass("no-zoom")
  this.container.find("img:first").css({"max-width": "100%"});
  this.objects.css({position: "absolute", "min-width": "30px"});

  this.saveObjectData = function(object) {
    EXERCISE.objects[$(object).data("index")].width = parseFloat(object.style.width).toString();
    EXERCISE.objects[$(object).data("index")].x = parseFloat(object.style.left).toString();
    EXERCISE.objects[$(object).data("index")].y = parseFloat(object.style.top).toString();
  }
  this.saveRecorderData = function(recorder) {
    EXERCISE.recorders[$(recorder).data("index")].x = parseFloat(recorder.style.left).toString();
    EXERCISE.recorders[$(recorder).data("index")].y = parseFloat(recorder.style.top).toString();
  }

  this.enableEdit = function() {
    this.objects.css({opacity: 0.7, background: "linear-gradient(90deg, rgba(255,255,255,0.3) 70%, rgba(0,0,0,0.3) 30%)"});
    this.recorders.css("background", "rgba(0,0,0,0.3)");
    var $this = this;
    this.objects.on("mousedown", function(mousedownEvent) {
      $this.editingObject = this;
      var object = this;
      var objectBounding = object.getBoundingClientRect();
      var containerBounding = $this.container[0].getBoundingClientRect();
      var objectLeft = object.offsetLeft;
      var objectTop = object.offsetTop;
      var objectWidth = object.width;

      function drag(mousemoveEvent) {
        var left = (100*(objectLeft + mousemoveEvent.clientX - mousedownEvent.clientX)/containerBounding.width ).toFixed(2);
        var top  = (100*(objectTop  + mousemoveEvent.clientY - mousedownEvent.clientY)/containerBounding.height).toFixed(2);
        $(object).css({left: left + "%", top: top + "%"});
      }
      function resize(mousemoveEvent) {
        var width   = (100*(objectWidth + mousemoveEvent.clientX - mousedownEvent.clientX)/containerBounding.width ).toFixed(2);
        $(object).css({width: width + "%"});
      }

      if((objectBounding.right - mousedownEvent.clientX)/objectBounding.width > 0.3) {
        document.addEventListener('mousemove', drag);
      } else {
        document.addEventListener('mousemove', resize);
      }
      function stopDrag() {
        document.removeEventListener('mousemove', drag);
        document.removeEventListener('mousemove', resize);
        document.removeEventListener('mouseup', stopDrag);
        $this.saveObjectData(object);
      }
      document.addEventListener('mouseup', stopDrag);
    });
    document.addEventListener('keydown', function(event) {
      if(!$this.editingObject || event.keyCode > 40 || event.keyCode < 37) return;
      event.preventDefault();
      var top = parseFloat($this.editingObject.style.top);
      var left = parseFloat($this.editingObject.style.left);
      switch(event.keyCode) {
        case 37:
          $($this.editingObject).css("left", left - 0.1 + "%");
          break;
        case 38:
          $($this.editingObject).css("top", top - 0.1 + "%");
          break;
        case 39:
          $($this.editingObject).css("left", left + 0.1 + "%");
          break;
        case 40:
          $($this.editingObject).css("top", top + 0.1 + "%");
          break;
      }
      $this.saveObjectData($this.editingObject);
    });
    this.recorders.on("mousedown", function(mousedownEvent) {
      var recorder = this;
      var objectBounding = recorder.getBoundingClientRect();
      var containerBounding = $this.container[0].getBoundingClientRect();

      var objectLeft = recorder.offsetLeft;
      var objectTop = recorder.offsetTop;
      function drag(mousemoveEvent) {
        var left = (100*(objectLeft + mousemoveEvent.clientX - mousedownEvent.clientX)/containerBounding.width ).toFixed(2);
        var top  = (100*(objectTop  + mousemoveEvent.clientY - mousedownEvent.clientY)/containerBounding.height).toFixed(2);
        $(recorder).css({left: left + "%", top: top + "%"});
      }
      function stopDrag() {
        document.removeEventListener('mousemove', drag);
        document.removeEventListener('mouseup', stopDrag);
        $this.saveRecorderData(recorder);
      }
      document.addEventListener('mousemove', drag);
      document.addEventListener('mouseup', stopDrag);
    });
  }

  this.enableEffect = function() {
    var $this = this;
    this.objects.css("opacity", 0);
    this.container.on("mousemove", function(event) {
      $this.objects.css("cursor", "auto");
      $this.objects.removeClass("shadow").css({"opacity": 0});
      if(event.target.tagName != "IMG") return;

      var hasHoverObject = false;
      $this.objects.each(function() {
        var bounding = this.getBoundingClientRect();
        if(event.clientX > bounding.left && event.clientX < bounding.right &&
           event.clientY > bounding.top  && event.clientY < bounding.bottom) {
          $this.context.clearRect(0, 0, $this.canvas.width, $this.canvas.height);
          $this.context.drawImage(this, 0, 0, this.width, this.height);
          if($this.context.getImageData(event.clientX - bounding.left, event.clientY - bounding.top, 1, 1).data[3] != 0) {
            $(this).addClass("shadow");
            $this.objects.css("cursor", "pointer");
            hasHoverObject = true;
            hoverCallBack && (this != $this.hoveringObject) && hoverCallBack(this);
            $this.hoveringObject = this;
            $this.objects.each(function() {
              if($(this).data("z-index") > $($this.hoveringObject).data("z-index")) {
                $(this).css("opacity", 1);
              }
            });
            return false;
          }
        }
      });
      if(!hasHoverObject) {
        $this.hoveringObject = null;
      }
    }).on("click", function(event) {
      if(event.target.tagName != "IMG" || !clickCallBack) return;
      $this.objects.each(function() {
        var bounding = this.getBoundingClientRect();
        if(event.clientX > bounding.left && event.clientX < bounding.right &&
           event.clientY > bounding.top  && event.clientY < bounding.bottom) {
          $this.context.clearRect(0, 0, $this.canvas.width, $this.canvas.height);
          $this.context.drawImage(this, 0, 0, this.width, this.height);
          if($this.context.getImageData(event.clientX - bounding.left, event.clientY - bounding.top, 1, 1).data[3] != 0) {
            clickCallBack(this);
            return false;
          }
        }
      });
    }).on("mouseout", function() {
      $this.objects.removeClass("shadow").css("cursor", "auto");
    });
  }
}

function UtilityTextNormalize(selector) {
  //chuan hoa
  $(selector).each(function() {
    var tempVal = $(this).text().toLowerCase().replace(/[^\w\d\s]/g, '').replace(/\s+/g, ' ').trim();
    var tempKey = $(this).attr("data-key").split("|");
    for (var i = 0; i < tempKey.length; i++) {
      if (tempVal == tempKey[i].toLowerCase().replace(/[^\w\d\s]/g, '').replace(/\s+/g, ' ').trim()) {
        $(this).text($(this).attr("data-key").split("|")[i].replace("[", "").replace("]", ""));
        break;
      }
    }
  });
}

// replace it's -> it is, i'm -> am
var dictionaryContractions =
  [["i'm", "i am"],["he's", "he is"],["she's", "she is"],["isn't", "is not"], ["it's", "it is"], ["there's", "there is"], ["there're", "there are"]];
function removeContractions(string) {
  string = string.toString();
  for (d in dictionaryContractions) {
    string = string.split(dictionaryContractions[d][0]).join(dictionaryContractions[d][1]);
  }
  return string;
}

